// Очікуємо завантаження вікна та запускаємо функцію
window.onload = function () {
    var boardElement;
    var wordList;
    var urlParams = new URLSearchParams(window.location.search);

    // Отримуємо розміри гри та значення slider2 з параметрів URL
    height = parseInt(urlParams.get('height')) || 6;
    width = parseInt(urlParams.get('width')) || 5;
    var slider2Value = parseInt(urlParams.get('slider2')) || 5;

    // console.log("Кількість ключів:", Object.keys(wordList).length);
    // console.log("Кількість значень:", Object.values(wordList).length);

    // Отримуємо словник слів для відповідності значення slider2
    wordList = getWordListBySliderValue(slider2Value);
    displayWordList(wordList);
    guessList = {};

    boardElement = document.getElementById("board");

    // Задаємо розміри дошки відповідно до параметрів гри
    if (boardElement) {
        boardElement.style.width = width * tileSize + "px";
        boardElement.style.height = height * tileSize + "px";
    }

    // Ініціалізуємо змінні для координат, стану гри та слова
    row = 0;
    col = 0;
    gameOver = false;

    // Копіюємо словник слів для подальшого відновлення
    guessList = Object.assign({}, guessList, wordList);
    let keys = Object.keys(wordList);
    word = keys[Math.floor(Math.random() * keys.length)].toUpperCase();
    // Ініціалізуємо гру
    initialize();
}

// Функція для створення гри та клавіатури
function initialize() {

    console.log("тут могла б бути ваша реклама");
    // console.log("Загадане слово: " + word);

    // Створюємо ігрову дошку
    for (let r = 0; r < height; r++) {
        for (let c = 0; c < width; c++) {
            let tile = document.createElement("span");
            tile.id = r.toString() + "-" + c.toString();
            tile.classList.add("tile");
            tile.innerText = "";
            document.getElementById("board").appendChild(tile);
        }
    }

    // Створюємо клавіатуру
    let keyboard = [
        ["Й", "Ц", "У", "К", "Е", "Н", "Г", "Ш", "Щ", "З", "Х", "Ї"],
        ["Ф", "І", "В", "А", "П", "Р", "О", "Л", "Д", "Ж", "Є", "'"],
        ["Enter", "Я", "Ч", "С", "М", "И", "Т", "Ь", "Б", "Ю", "⌫"]
    ];

    for (let i = 0; i < keyboard.length; i++) {
        let currRow = keyboard[i];
        let keyboardRow = document.createElement("div");
        keyboardRow.classList.add("keyboard-row");
    
        for (let j = 0; j < currRow.length; j++) {
            let keyTile = document.createElement("div");
    
            let key = currRow[j];
            keyTile.innerText = key;
            keyTile.id = "Key" + key.toUpperCase();
            keyTile.addEventListener("click", function () {
                processKey(key);
            });
    
            // Додаємо класи для стилізації клавіш
            if (key == "Enter") {
                keyTile.classList.add("enter-key-tile");
            } else if (key == "Backspace") {
                keyTile.classList.add("backspace-key-tile");
            } else {
                keyTile.classList.add("key-tile");
            }
            keyboardRow.appendChild(keyTile);
        }
        document.body.appendChild(keyboardRow);
    }

    // Оброблюємо події натискання клавіші на клавіатурі
    document.addEventListener("keydown", function (e) {
        if (e.key === "Enter" || e.key === "Backspace") {
            e.preventDefault();
            processKey(e.key);
        } else {
            processKey(e.key.toUpperCase());
        }
    });
}

// Змінні для зберігання кількості спроб та стану гри
let attempts = 0;

// Функція для обробки натискання клавіші
function processKey(key) {
    if (gameOver) return;

    if (/^[А-ЩЬЮЯЇІЄҐ]$/.test(key.toUpperCase()) || key == " ' " || key == "'") {
        if (col < width) {
            let currTile = document.getElementById(row.toString() + '-' + col.toString());
            if (currTile.innerText == "") {
                currTile.innerText = key.toUpperCase();
                col += 1;
            }
        }
    } else if (key === "⌫" || key === "Backspace") {
        if (0 < col && col <= width) {
            col -= 1;
        }
        let currTile = document.getElementById(row.toString() + '-' + col.toString());
        currTile.innerText = "";
    } else if (key === "Enter") {
        update();

        // Виводимо загадане слово після завершення гри (перемога чи поразка)
        if (gameOver) {
            document.getElementById("answer").innerText = word;
            document.getElementById("definition").innerText = guessList[word.toLowerCase()];
        }
    }
}

// Функція для оновлення гри після введення спроби
function update() {
    document.getElementById("definition").innerText = "";

    let guess = "";
    document.getElementById("answer").innerText = "";

    // Зчитуємо введені літери
    for (let c = 0; c < width; c++) {
        let currTile = document.getElementById(row.toString() + '-' + c.toString());
        let letter = currTile.innerText;
        guess += letter;
    }

    guess = guess.toLowerCase();

    let lowerCaseGuess = guess.toLowerCase();
    // Перевіряємо, чи введене слово є в словнику слів
    if (!Object.keys(guessList).includes(lowerCaseGuess)) {
        document.getElementById("answer").innerText = "Вибачте, такого слова, поки що, немає в нашому словнику :(";
        return;
    }

    let correct = 0;
    let letterCount = {};

    // Підраховуємо кількість кожної літери в слові
    for (let i = 0; i < word.length; i++) {
        let letter = word[i];

        if (letterCount[letter]) {
            letterCount[letter] += 1;
        }
        else {
            letterCount[letter] = 1;
        }
    }

    // Перевіряємо введені літери і оновлюємо відповідно
    for (let c = 0; c < width; c++) {
        let currTile = document.getElementById(row.toString() + '-' + c.toString());
        let letter = currTile.innerText;

        if (word[c] == letter) {
            currTile.classList.add("correct");

            let keyTile = document.getElementById("Key" + letter);
            if (keyTile) {
                keyTile.classList.remove("present-key", "absent-key");
                keyTile.classList.add("correct-key");
            }

            correct += 1;
            letterCount[letter] -= 1;
        }

        if (correct == width) {
            // Виводимо повідомлення про перемогу
            document.getElementById("result").innerText = "Вітаю, ви перемогли!\nВи вгадали слово";
            document.getElementById("result").style.color = "#6AAA64"; 
            gameOver = true;
        }
    }

    // Перевіряємо решту введених літер і оновлюємо відповідно
    for (let c = 0; c < width; c++) {
        let currTile = document.getElementById(row.toString() + '-' + c.toString());
        let letter = currTile.innerText;

        if (!currTile.classList.contains("correct")) {
            if (word.includes(letter) && letterCount[letter] > 0) {
                currTile.classList.remove("absent");
                currTile.classList.add("present");

                let keyTile = document.getElementById("Key" + letter);
                if (keyTile) {
                    keyTile.classList.remove("absent-key");
                    keyTile.classList.add("present-key");
                }
                letterCount[letter] -= 1;
            } else {
                currTile.classList.remove("present");
                currTile.classList.add("absent");

                let keyTile = document.getElementById("Key" + letter);
                if (keyTile) {
                    keyTile.classList.remove("present-key", "correct-key");
                    keyTile.classList.add("absent-key");
                }
            }
        }
    }

    attempts += 1; // Ітеруємо спробу

    // Виводимо повідомлення про поразку, якщо досягнуто ліміт спроб і користувач не вгадав слово
    if (attempts >= height && !gameOver) {
        document.getElementById("result").innerText = "На жаль, ви програли :(\nЗагаданим словом було";
        document.getElementById("result").style.color = "#FF0000"; 
        document.getElementById("definition").innerText = guessList[lowerCaseGuess];
        document.getElementById("answer").innerText = word;
        gameOver = true;
    }

    row += 1;
    col = 0;
}

