// Обробка кліків на кнопках "reloadButton" та "darkThemeToggle" з врахуванням всплеску подій
document.addEventListener("DOMContentLoaded", function () {
    var reloadButton = document.getElementById("reloadButton");
    var darkThemeToggle = document.getElementById("darkThemeToggle");

    reloadButton.addEventListener("click", function () {
        location.reload();
    });

    darkThemeToggle.addEventListener("click", function (event) {
        event.stopPropagation();
        document.body.classList.toggle("dark-theme");
    });

    // Елементи для роботи з попапами
    var questionMark = document.querySelector(".fa-regular.fa-circle-question");
    var wordsListButton = document.querySelector(".button");
    var popup = document.getElementById("popup");
    var closePopup = document.getElementById("close-popup");
    var popupWordsList = document.getElementById("popupWordsList");
    var closePopupWordsList = document.getElementById("close-popup-words-list");
    var ignoreIcons = document.querySelectorAll(".navbar i, .fa-brands.fa-github, .fa-brands.fa-linkedin");

    // Функції для анімації попапів
    function togglePopup(popupElement, closeButton) {
        if (popupElement.style.display === "block") {
            closePopupHandler(popupElement);
        } else {
            openPopupHandler(popupElement);
        }
    }

    function openPopupHandler(popupElement) {
        popupElement.style.display = "block";
        popupElement.style.opacity = 0;
        popupElement.classList.add("show");

        setTimeout(function () {
            popupElement.style.opacity = 1;
        }, 50);
    }

    function closePopupHandler(popupElement) {
        popupElement.style.opacity = 0;
        popupElement.classList.remove("show");

        setTimeout(function () {
            popupElement.style.display = "none";
        }, 500);
    }

    // Обробка кліку на питання для відображення відповідного попапу та ігнорування інших
    questionMark.addEventListener("click", function (event) {
        event.stopPropagation();
        togglePopup(popup, closePopupWordsList);
        closePopupHandler(popupWordsList);
    });

    // Закриття попапа за допомогою кнопки "closePopup"
    closePopup.addEventListener("click", function () {
        closePopupHandler(popup);
    });

    // Обробка кліку на кнопці "wordsListButton" для відображення відповідного попапу та ігнорування інших
    wordsListButton.addEventListener("click", function (event) {
        event.stopPropagation();
        togglePopup(popupWordsList, closePopup);
        closePopupHandler(popup);
    });

    // Закриття попапа за допомогою кнопки "closePopupWordsList"
    closePopupWordsList.addEventListener("click", function () {
        closePopupHandler(popupWordsList);
    });

    // Закриття попапа при кліку поза ним або на певних іконках
    document.addEventListener("click", function (event) {
        var target = event.target;
        var isGitHubIcon = target.matches(".fa-brands.fa-github");
        var isLinkedInIcon = target.matches(".fa-brands.fa-linkedin");

        if (questionMark.contains(target) || isGitHubIcon || isLinkedInIcon) {
            return;
        }

        if (target === wordsListButton) {
            return;
        }

        closePopupHandler(popup);
    });

    // хз що це, може взагалі не треба
    function containsAny(target, elements) {
        return Array.from(elements).some(function (element) {
            return element.contains(target);
        });
    }
});

