// Відображення списку слів на сторінці
function displayWordList(wordList) {
    var wordListContent = document.getElementById("wordList-content");
    wordListContent.innerHTML = "";

    var wordListHTML = "<p>";
    var wordsArray = Object.keys(wordList);

    for (var i = 0; i < wordsArray.length; i++) {
        wordListHTML += wordsArray[i];

        if (i < wordsArray.length - 1) {
            wordListHTML += ", ";
        }
    }

    wordListHTML += "</p>";
    wordListContent.innerHTML = wordListHTML;
}
